import React, { Component } from 'react';
import { Joi } from 'joi-browser';


class Register extends Component {
  state = {
    username: '',
    email: '',
    password: '',
  }

  schema = {
    username: Joi.string().min(3).max(45),
    email: Joi.string().min(3).max(255),
  }

  render() { 
    return (
      <React.Fragment>
      <div style={style.container}></div>
        <div style={style.popup}>
          <h2 style={style.header}>Register</h2>

          <div style={style.spacing}>
            <span style={style.text}>Username</span>
            <input type="text" style={style.input} placeholder="Username" />
          </div>

          <div style={style.spacing}>
            <span style={style.text}>Email</span>
            <input type="text" style={style.input} placeholder="Email" / >
          </div>

          <div style={style.spacing}>
            <span style={style.text}>Password</span>
            <input type="text" style={style.input} placeholder="Password" / >
          </div>

          <div style={style.submit}>Submit</div>
        </div>
      </React.Fragment>
    );
  }
}
export default Register;


const style ={
  spacing: {
    marginBottom: 5,
  },

  input: {
    width: 250,
    textAlign: 'center',
  },

  text: {
    display: 'inline-block',
    width: 130,
    padding: '0px 30px',
  },

  submit: {
    position: 'relative',
    width: 100,
    textAlign: 'center',
    padding: '4px 10px',
    backgroundColor: 'black',
    color: 'white',
    borderRadius: 5,
    marginLeft: "50%",
    left: -50,
    marginTop: 20,
    cursor: 'pointer',
    tabIndex: 1,
  },

  popup: {
    width: 410,
    height: 230,
    position: 'fixed', 
    top: "50%", left: "50%", 
    marginTop: -115, marginLeft: -205,
    borderRadius: 15,
    border: '1px solid black',
    backgroundColor: 'white',
    boxShadow: '2px 2px 2px black',
  },
  header: {
    textAlign: 'center',
    paddingTop: 5,
  },
  container: {
    width: '100vw',
    height: '100vh',
    position: 'fixed',
    top: 0,
    left: 0,
    backgroundColor: 'black',
    opacity: 0.6,
  },

}
